#[test]
#[cfg(not(feature = "no_closure"))]
#[cfg(not(feature = "no_function"))]
#[cfg(not(feature = "no_object"))]
fn test_stack_overflow_ref_loop() {
    let engine = Engine::new();

    let x = engine
        .eval::<Dynamic>(
            "
                let x;
                let data = #{ foo: || x = this };
                data.foo();
                x
            ",
        )
        .unwrap();

    println!("{x:?}");
}
